import React from 'react'
import SimpleCheckbox from '../material-kit/checkbox/SimpleCheckbox'
import LabelledCheckbox from '../material-kit/checkbox/LabelledCheckbox'
import PlacingCheckboxLabel from '../material-kit/checkbox/PlacingCheckboxLabel'
import FormGroupCheckbox from '../material-kit/checkbox/FormGroupCheckbox'
import {SimpleCard} from 'app/components'
import {Box, styled} from '@mui/system'
import {Button, Icon} from "@mui/material";
import {Span} from "../../components/Typography";
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator'

const Container = styled('div')(({theme}) => ({
    margin: '30px',
    [theme.breakpoints.down('sm')]: {
        margin: '16px',
    },
    '& .breadcrumb': {
        marginBottom: '30px',
        [theme.breakpoints.down('sm')]: {
            marginBottom: '16px',
        },
    },
}))

const Test = () => {

    const [state, setState] = React.useState({
        isSubmitted: false,
    })
    const [responseData, setResponseData] = React.useState({
        name: '',
        value: '',
    })
    const swUrl = `https://metrics-351617.rj.r.appspot.com/metrics/`

    const handleSubmit = (event) => {
        fetch(swUrl)
            .then(response => response.json())
            .then(response => {
                console.log(response)
                setResponseData(response)
                setState({isSubmitted: true})
            })
            .catch((error) => {
                console.log(
                    'error: ' + error
                )
            })
    }

    return (
        <Container>
            <div>
                <ValidatorForm onSubmit={handleSubmit} onError={() => null}>
                    {state.isSubmitted && responseData}
                    <Button color="primary" variant="contained" type="submit">
                        <Icon>send</Icon>
                        <Span sx={{pl: 1, textTransform: 'capitalize'}}>
                            Submit
                        </Span>
                    </Button>
                </ValidatorForm>
            </div>
            <SimpleCard title="simple checkbox">
                <SimpleCheckbox/>
            </SimpleCard>
            <Box py="12px"/>
            <SimpleCard title="Checkbox with Label">
                <LabelledCheckbox/>
            </SimpleCard>
            <Box py="12px"/>
            <SimpleCard title="Checkbox with Form Group">
                <FormGroupCheckbox/>
            </SimpleCard>
            <Box py="12px"/>
            <SimpleCard title="Checkbox with Different Label Placement">
                <PlacingCheckboxLabel/>
            </SimpleCard>
        </Container>
    )
}

export default Test
