import React, {Fragment, lazy, useState} from 'react'
import {Autocomplete, Button, Grid, Icon, TextField} from '@mui/material'
import {createFilterOptions} from '@mui/material/Autocomplete'
import {styled, useTheme} from '@mui/system'
import Loadable from "../../components/Loadable/Loadable"
import AdapterDateFns from "@mui/lab/AdapterDateFns";
import {DatePicker} from "@mui/lab";
import LocalizationProvider from "@mui/lab/LocalizationProvider";
import {ValidatorForm} from "react-material-ui-form-validator";
import {Span} from "../../components/Typography";

const Result = Loadable(lazy(() => import("./Result")))
const MetricChart = Loadable(lazy(() => import("./MetricChart")))

const AutoComplete = styled(Autocomplete)(() => ({
    width: 300,
    marginBottom: '16px',
}))

const CalculateMetricView = () => {
    const theme = useTheme()

    const [status, setStatus] = React.useState(false)
    const [metrics, setMetrics] = React.useState([]);
    const [source, setSource] = React.useState([])
    const [valueCombo, setValueCombo] = React.useState(null)
    const [state, setState] = useState({
        date: new Date(),
    })

    const [labelsX, setLabelsX] = React.useState([])

    const handleChange = (event) => {
        event.persist()
        setState({
            ...state,
            [event.target.name]: event.target.value,
        })
    }

    const handleComboChange = (event, newValue) => {
        if (newValue && newValue.inputValue) {
            setValueCombo({
                label: newValue.inputValue,
            })
            return
        }

        setValueCombo(newValue)

    }

    const handleDateChange = (date) => {
        setState({...state, date})
    }

    const handleSubmit = (event) => {

        var url = 'https://metrics-351617.rj.r.appspot.com/metrics/calc';
        var data = {
            dateFrom: nameParam,
            timeType: "",
            metricName: ""
        };

        fetch(url, {
            method: 'POST',
            body: JSON.stringify(data),
            headers:{
                'Content-Type': 'application/json'
            }
        }).then(res => res.json())
            .catch(error => console.error('Error:', error))
            .then(response => console.log('Success:', response));

        reload()

    }

    const getData = async () => {
        if(!status){
            const resp = await fetch("https://metrics-351617.rj.r.appspot.com/metrics/");
            const data = await resp.json();
            const metricList = []
            data.content.map((metric, index) => (
                metricList.push(metric.name)
            ))
            setMetrics(data.content);
            setStatus(true);
        }
    };

    const reload=()=>window.location.reload();

    const {
        name,
        value,
        date,
    } = state

    const suggestions = [
        {label: 'MINUTE', value: 'MINUTE'},
        {label: 'HOUR minutes', value: 'HOUR'},
        {label: 'DAY', value: 'DAY'},
        {label: 'MONTH', value: 'MONTH'},
    ]

    return (
        <Fragment>
            <ValidatorForm onSubmit={handleSubmit} onError={() => null}>
                <Grid container spacing={6}>
                    <Grid item lg={6} md={6} sm={12} xs={12} sx={{mt: 2}}>
                        <AutoComplete
                            options={suggestions}
                            getOptionLabel={(option) => option.label}
                            onChange={handleComboChange}
                            renderInput={(params) => (
                                <TextField
                                    {...params}
                                    label="Select param time"
                                    variant="outlined"
                                    fullWidth
                                />
                            )}
                        />
                        <AutoComplete
                            options={suggestions}
                            getOptionLabel={(option) => option.label}
                            onChange={handleComboChange}
                            renderInput={(params) => (
                                <TextField
                                    {...params}
                                    label="Select metric name"
                                    variant="outlined"
                                    fullWidth
                                />
                            )}
                        />

                        <LocalizationProvider dateAdapter={AdapterDateFns}>
                            <DatePicker
                                value={date}
                                onChange={handleDateChange}
                                renderInput={(props) => (
                                    <TextField
                                        {...props}
                                        // variant="Outlined"
                                        id="mui-pickers-date"
                                        label="Date"
                                        sx={{mb: 2, width: '100%'}}
                                    />
                                )}
                            />
                        </LocalizationProvider>
                    </Grid>

                </Grid>

                <Button color="primary" variant="contained" type="submit" onSubmit={handleSubmit}>
                    <Icon>send</Icon>
                    <Span sx={{pl: 1, textTransform: 'capitalize'}}>
                        Send
                    </Span>
                </Button>
                <div>
                    {state.date.toLocaleString()}
                </div>
            </ValidatorForm>









            {
            <MetricChart
                height="350px"
                color={[
                    theme.palette.primary.dark,
                    theme.palette.primary.light,
                ]}
                values={source}
            />
            }

        </Fragment>
    )
}

export default CalculateMetricView
