import React, {Fragment, lazy} from 'react'
import { TextField, Autocomplete } from '@mui/material'
import { createFilterOptions } from '@mui/material/Autocomplete'
import { styled } from '@mui/system'
import Loadable from "../../components/Loadable/Loadable"
const Result = Loadable(lazy(() => import("./Result")))
const MetricChart = Loadable(lazy(() => import("./MetricChart")))

const AutoComplete = styled(Autocomplete)(() => ({
    width: 300,
    marginBottom: '16px',
}))

const suggestions = [
    { label: 'Past 5 minutes', value: '0'},
    { label: 'Past 15 minutes', value: '1' },
    { label: 'Past 30 minutes', value: '2' },
    { label: 'Past 1 hour', value: '3' },
    { label: 'Past 4 hours', value: '4' },
    { label: 'Past 10 hours', value: '5' },
    { label: 'Past 1 day', value: '6' },
    { label: 'Past 2 days', value: '7' },
    { label: 'Past 7 days', value: '8' },
]

const filter = createFilterOptions()

const AutocompleteCombo = () => {
    const [value, setValue] = React.useState(null)
    const [state, setState] = React.useState({
        isSubmitted: false,
    })
    const [responseData, setResponseData] = React.useState({
        name: '',
        value: '',
    })
    const [labelsX, setLabelsX] = React.useState([])
    const [labelsY, setLabelsY] = React.useState([])

    const handleChange = (event, newValue) => {
        if (newValue && newValue.inputValue) {
            setValue({
                label: newValue.inputValue,
            })
            return
        }

        setValue(newValue)

        const swUrl = `https://metrics-351617.rj.r.appspot.com/metrics/`
        fetch(swUrl)
            .then(response => response.json())
            .then(response => {
                console.log(response)
                setResponseData(response)
                setState({isSubmitted: true})
            })
            .catch((error) => {
                console.log(
                    'error: ' + error
                )
            })

        calculateValuesAxisX(newValue.value)

    }

    const filterOptions = (options, params) => {
        const filtered = filter(options, params)
        if (params.inputValue !== '') {
            filtered.push({
                inputValue: params.inputValue,
                label: `Add "${params.inputValue}"`,
            })
        }
        return filtered
    }


    const calculateValuesAxisX = (paramTime) => {

        const current = new Date();
        const myPastDate=new Date(current);

        // 5 minutes
        if (paramTime == 0){
            myPastDate.setDate(myPastDate.getMinutes() - 5)
            const initialDate = myPastDate;

            for (var i = 0; i < 5; i++) {
                labelsX[i] = `${initialDate.getHours()}:${initialDate.getMinutes()}`
                initialDate.setDate(initialDate.getMinutes() + 1)
            }
        }

        // 15 minutes
        if (paramTime == 1){
            myPastDate.setDate(myPastDate.getMinutes() - 15)
            const initialDate = myPastDate;

            for (var i = 0; i < 5; i++) {
                labelsX[i] = `${initialDate.getHours()}:${initialDate.getMinutes()}`
                initialDate.setDate(initialDate.getMinutes() + 3)
            }
        }

        // 30 minutes
        if (paramTime == 2){
            myPastDate.setDate(myPastDate.getMinutes() - 30)
            const initialDate = myPastDate;

            for (var i = 0; i < 5; i++) {
                labelsX[i] = `${initialDate.getHours()}:${initialDate.getMinutes()}`
                initialDate.setDate(initialDate.getMinutes() + 6)
            }
        }


    }

    return (
        <Fragment>
            <AutoComplete
                options={suggestions}
                getOptionLabel={(option) => option.label}
                onChange={handleChange}
                renderInput={(params) => (
                    <TextField
                        {...params}
                        label="Combo box"
                        variant="outlined"
                        fullWidth
                    />
                )}
            />
            {state.isSubmitted}{

            <MetricChart
                sourceValuesX={labelsX}
                sourceValuesY={labelsY}
                values={''}
            />

        }

        </Fragment>
    )
}

export default AutocompleteCombo
